const l=n=>/^\d{4}-\d{2}-\d{2}$/.test(n.trim()),m=n=>{if(!n)return"";const t=n.trim();if(!t)return"";if(l(t))return t;const d=t.split("/");if(d.length===3){const[e,s,r]=d;if(r.length===4){const o=e.padStart(2,"0"),i=s.padStart(2,"0");return`${r}-${i}-${o}`}}const a=t.split("-");if(a.length===3&&a[2].length===4){const[e,s,r]=a,o=e.padStart(2,"0"),i=s.padStart(2,"0");return`${r}-${i}-${o}`}const c=new Date(t);return isNaN(c.getTime())?"":c.toISOString().slice(0,10)};export{m as t};
